} from '@flydiy/login';
import {
    translateFn
} from './detailthree.component.translate';
// 防止用户多添加参数或者少添加参数报错
const $event = '参数异常';
const rowData = '参数异常';
export class DetailthreeComponentAuto {
    $event = '参数异常';
    rowData = '参数异常';
    // 存储路由参数
    routerParams: any = {};
    // 外部传进来的主对象的参数
    @Input() mainObjectParams: any;


    // 存储表单数据
    @Input() data: any = {

    };

    @Output() dataChange: EventEmitter < any > = new EventEmitter();


    TranslateService: TranslateService;
    UserService: UserService;
    ActivatedRoute: ActivatedRoute;
    constructor(public Injector: Injector) {

        this.TranslateService = this.Injector.get(TranslateService);
        this.UserService = this.Injector.get(UserService);
        this.ActivatedRoute = this.Injector.get(ActivatedRoute);

        this.ActivatedRoute.queryParams.subscribe((queryParams: any) => {
            this.routerParams = queryParams;
        });
        translateFn()(this.TranslateService);

    }
    ngOnInit() {


    }


    // 父子孙类型的数据，在前端和后端格式是不一样的，要先转换之后，再传递到后端
    transformFormData() {
        let data = angular.copy(this.data);

        // 算出有没有使用父子孙
        let sunKey, suenKey;
        for (let key of Object.keys(data)) {
            let objects = data[key];
            if (angular.isArray(objects)) {
                // 不一定每一个子都有孙，所以要遍历
                let isSon = false;
                for (let keyT of objects) {
                    if (keyT.$children) {
                        isSon = true;
                    }
                }
                if (objects.length > 0 && isSon) {
                    sunKey = key; // 子
                } else {
                    suenKey = key; // 孙
                }
            }
        }

        // 是父子孙,就对数据进行转换
        if (sunKey && suenKey) {
            let sunItems = data[sunKey];
            let suenItems = data[suenKey];
            for (let sun of sunItems) {
                if (sun.$children) {
                    suenItems.push(sun.$children);
                } else {
                    suenItems.push([]);
                }
                delete sun.$children;
            }
        }
        return data;
    }





}

