import {
    TranslateService
} from "@ngx-translate/core";

export const translateFn = function translateFn() {
    return function(TranslateService: TranslateService) {
        let config = {}

        for (let language of Object.keys(config)) {
            let translates = config[language];
            TranslateService.setTranslation(language, translates, true);
        }
    }
}
